class Solution {
    public int maximumLength(int[] nums, int k) {
        int arr[] = new int[55];
        int dp[][] = new int[nums.length][k+5];
        HashMap<Integer,Integer> hm = new HashMap<>();
        int fans = 0;
        for(int i = 0 ; i < nums.length ; i++){
            int brr[] = new int[55];
            for(int j = 0 ; j <= k ; j++){
                int ans = 0;
                if(hm.containsKey(nums[i])){
                    ans = Math.max(ans , dp[hm.get(nums[i])][j]+1);
                }
                ans = Math.max(ans , arr[j+1]+1);
                dp[i][j] = ans;
                fans = Math.max(fans , ans);
                brr[j] = ans;
            }
            for(int j = 0 ; j < 55 ; j++)arr[j] = Math.max(arr[j],brr[j]);
            hm.put(nums[i],i);
        }
        return fans;
    }
}
