class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        res = [0] * (k + 1)
        best = defaultdict(lambda: [0] * (k + 1))

        for v in nums:
            best[v][0] += 1
            for i in range(1, k + 1):
                best[v][i] = max(res[i - 1] + 1, best[v][i] + 1)
            for i in range(k + 1):
                res[i] = max(res[i], best[v][i])

        return max(res)
