class Solution {

public:
    static const int N=5e3;
    int mx[55];
    int dp[N+1][55];
    vector<int> c;
    int maximumLength(vector<int>& a, int k) {
        int n=a.size();
        for(int x:a)c.push_back(x);
        c.push_back(0);
        sort(c.begin(),c.end());
        c.resize(unique(c.begin(),c.end())-c.begin());
        for(int &x:a){
            x=lower_bound(c.begin(),c.end(),x)-c.begin();
        }
        int ans=0;

        for(int i=1;i<=n;i++){
            int x=a[i-1];
            for(int j=k;j>=0;j--){
                dp[x][j]++;
                if(j)dp[x][j]=max(dp[x][j],1+mx[j-1]);
                mx[j]=max(mx[j],dp[x][j]);
                ans=max(ans,mx[j]);
            }
            // for(int i=0;i<=k;i++)cout<<mx[i]<<" ";cout<<endl;
        }
        return ans;
    }
};
