var maximumLength = function(gibberish, k) {
    let n = gibberish.length;
    let dp = Array.from({ length: n }, () => Array(k + 1).fill(1));
    let ans = 1;
    let vk = Array(k + 1).fill(-1);
    let vkm = Array.from({ length: k + 1 }, () => new Map());

    for (let i = 0; i < n; i++) {
        for (let j = 0; j <= k; j++) {
            for (let l = 0; l < j; l++) {
                dp[i][j] = Math.max(dp[i][j], 1 + vk[l]);
            }
            if (vkm[j].has(gibberish[i])) {
                dp[i][j] = Math.max(dp[i][j], 1 + vkm[j].get(gibberish[i]));
            }
            ans = Math.max(ans, dp[i][j]);
        }
        for (let j = 0; j <= k; j++) {
            vk[j] = Math.max(vk[j], dp[i][j]);
            vkm[j].set(gibberish[i], Math.max(vkm[j].get(gibberish[i]) || 0, dp[i][j]));
        }
    }
    return ans;
};
