class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        map<int,int>preM;
        int n=nums.size();
        int ans=1;
        for(int j=0;j<=k;j++)
        {
            map<int,int>nP;
            map<int,int>sameM;
            for(int i=0;i<n;i++)
            {
                int curr=sameM[nums[i]]+1;
                curr=max(curr,preM[i-1]+1);
                ans=max(ans,curr);
                nP[i]=max(nP[i-1],curr);
                sameM[nums[i]]=curr;
            }
            preM=nP;
        }
        return ans;
    }
};
