class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        int dp[n+1][k+1],an=1;
        map<int,int> m;
        for(int i=0;i<n+1;i++)
        for(int j=0;j<k+1;j++) {
            dp[i][j]=0;
        }

        vector<int> v(n,0);
        int ma=0;
        for(int i=0;i<n;i++) {
            v[i]=ma;
            m[nums[i]]++;
            dp[i][0]=m[nums[i]];
            an=max(an, dp[i][0]);
            ma=max(dp[i][0],ma);
        }

        set<int> s;
        for(int l=1;l<=k;l++) {
            map<int,int> m2;
            int ma=0;
            for(int i=0;i<n;i++) {
                dp[i][l] = max(m2[nums[i]]+1,v[i]+1);
                v[i]=ma;
                an=max(an, dp[i][l]);
                m2[nums[i]]=dp[i][l];
                ma=max(v[i],dp[i][l]);
            }
        }
        return an;

    }
};
