class Solution(object):
    def maximumLength(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        n = len(nums)



        ans = 1


        dp = [0]*n


        dic = {}
        for i in range(n):
            if nums[i] not in dic:  dic[nums[i]] = 0
            dic[nums[i]] += 1
            dp[i] = dic[nums[i]]


       # print(dp)


        for _ in range(k):

            currmax = 1
            newdp = [1]*n

            dic = {}
            for i in range(1,n):

                newdp[i] = currmax + 1
                if nums[i] in dic:
                    newdp[i] = max(newdp[i], dic[nums[i]] + 1)

                dic[nums[i]] = newdp[i]
                currmax = max(currmax,  dp[i])

           # print(newdp,dic)

            dp = newdp




        return max(dp)
