class Solution {
public:

    #define ll long long
    #define F(i, q, n) for(ll i=q; i<=n; i++)
    #define f(i, n) for(ll i=0; i<n; i++)
    #define vll vector<ll>
    // ll func(ll i, ll n, vector<vector<ll>> &dp) {
    // }
    vll arr;

    int maximumLength(vector<int>& nums, int k) {
        ll n = nums.size(); 
        ll ans = 1; 
        arr.assign(k+1,-1); 
        vector<vll> dp(n, vll(k + 1, 1)); 
        vector<map<ll, ll>> arrCnt(k + 1); 
        f(i, n){ 
            F(j, 0, k){ 
                f(l, j) dp[i][j] = max(dp[i][j], 1 + arr[l]); 

                if(arrCnt[j].count(nums[i])) dp[i][j]=max(dp[i][j],1+arrCnt[j][nums[i]]); 
                ans = max(ans, dp[i][j]); 
            } 
            F(j, 0, k){ 
                arr[j] = max(dp[i][j], arr[j]); 
                arrCnt[j][nums[i]] = max(dp[i][j], arrCnt[j][nums[i]]); 
            } 
        } 
        return ans; 
    }
};
