class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        map<int,int> mp[k+1];
        //mp[goodLength][lastElement] = Totallength
        //st[goodLength] = {Totallength, lastElement}
        //
        int ans=1;
        set<array<int,2>> st[k+1];
        for(int i=0;i<nums.size();i++){
            for(int j=0;j<=k;j++){
                // if(j==0){
                //     mp[0][nums[i]]=;
                //     st[0].insert({0, nums[i]});
                // }
                int cur = nums[i];
                int mx = mp[j][cur]+1;

                if(j>0){
                    for (auto it = st[j-1].rbegin(); it != st[j-1].rend(); it++) {
                        if((*it)[1]!=cur){
                            mx=max(mx, (*it)[0]+1);
                            break;
                        }
                    }
                }


                if(st[j].find({mp[j][cur], cur}) != st[j].end())
                    st[j].erase({mp[j][cur], cur});
                mp[j][cur]=mx;

                st[j].insert({mp[j][cur], cur});
                ans=max(ans, mx);

            }
        }
        return ans;
    }
};
