from typing import List

class Solution:
    def maximumLength(self, arr: List[int], target: int):
        length = len(arr)
        memo = [[1] * (target + 1) for _ in range(length)]
        max_length = 1
        prev_max = [-1] * (target + 1)
        prev_max_map = [{} for _ in range(target + 1)]

        for i in range(length):
            self.update_memo(arr, memo, prev_max, prev_max_map, target, i)
            max_length = self.update_max_length(memo, max_length, i, target)

        return max_length

    def update_memo(self, arr, memo, prev_max, prev_max_map, target, i):
        for j in range(target + 1):
            for k in range(j):
                memo[i][j] = max(memo[i][j], 1 + prev_max[k])
            if arr[i] in prev_max_map[j]:
                memo[i][j] = max(memo[i][j], 1 + prev_max_map[j][arr[i]])

        for j in range(target + 1):
            prev_max[j] = max(prev_max[j], memo[i][j])
            prev_max_map[j][arr[i]] = max(prev_max_map[j].get(arr[i], -1), memo[i][j])

    def update_max_length(self, memo, max_length, i, target):
        for j in range(target + 1):
            max_length = max(max_length, memo[i][j])
        return max_length
