#include<bits/stdc++.h>
class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        vector dp(n,vector<int>(k+1,1));
        vector<int> super(k+1,1);
        map<int,int> pos;
        pos[nums[0]]=0;
        for(int i=1;i<n;i++){
            if(pos.count(nums[i])){
                for(int l=0;l<=k;l++){
                    dp[i][l]=max(dp[i][l],dp[pos[nums[i]]][l]+1);
                }
            }
            for(int l=0;l<k;l++){
                dp[i][l+1]=max(dp[i][l+1],super[l]+1);
            }
            for(int l=0;l<=k;l++){
                super[l]=max(super[l],dp[i][l]);
            }
            pos[nums[i]]=i;
        }
        return super[k];
    }
};
