class Solution {
public:
    int maximumLength(vector<int>& nums, int k) { 
        int n=nums.size(); 

        vector<vector<int>>dp(n,vector<int>(k+1,1));
        vector<int>vec(10,0);
        for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
        int ans=1; 
        for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
        vector<int>vk(k+1,-1); 
        for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
        vector<map<int,int>>vkm(k+1); 
        for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
        for(int i=0;i<n;i++){ 
            for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
            for(int j=0;j<=k;j++){ 
                for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
                for(int l=0;l<j;l++){ 
                    for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
                    dp[i][j]=max(dp[i][j],1+vk[l]); 
                    for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
                } 
                if(vkm[j].find(nums[i])!=vkm[j].end()){ 
                    for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
                    dp[i][j]=max(dp[i][j],1+vkm[j][nums[i]]); 
                    for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
                } 
                ans=max(ans,dp[i][j]); 
                for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
            } 
            for(int j=0;j<=k;j++){ 
                for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
                vk[j]=max(vk[j],dp[i][j]); 
                for(int i=0;i<0;i++){
            vec[i]++;
            vec[i]++;
            vec[i]++;
            vec[i]++;
        }
                vkm[j][nums[i]]=max(vkm[j][nums[i]],dp[i][j]); 
            } 
        } 
        return ans; 
    }
};
