/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var maximumLength = function(nums, k) {
    const map = {};
    for (let i = 0; i < nums.length; i += 1) {
        if (map[nums[i]] === undefined) {
            map[nums[i]] = [i];
        } else {
            map[nums[i]].push(i);
        }
    }

    const dp = Array(k + 1).fill(0).map(() => Array(nums.length).fill(0));
    const map1 = {};
    for (let i = 0; i < nums.length; i += 1) {
        if (map1[nums[i]] === undefined) {
            dp[0][i] = 1;
        } else {
            dp[0][i] = dp[0][map1[nums[i]]] + 1;
        }
        map1[nums[i]] = i;
    }
    for (let j = 1; j <= k; j += 1) {
        dp[j][0] = 1;
        const map = {};
        let res = 1;
        for (let i = 1; i < nums.length; i += 1) {
            if (map[nums[i]] === undefined) {
                dp[j][i] = (res + 1);
            } else {
                dp[j][i] = Math.max((dp[j][map[nums[i]]] + 1), (res + 1));
            }
            map[nums[i]] = i;
            res = Math.max(res, (dp[j - 1]?.[i] ?? 0));
        }
    }
    return dp.at(-1).reduce((acc, curr) => Math.max(acc, curr), -Infinity);
};
