class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();

        vector<vector<int>> dp(k + 1, vector<int>(n));


        map<int, int> freq;
        for(int i: nums) freq[i]++;

        for(int i = n - 1; i >= 0; i--){
            dp[0][i] = freq[nums[i]];
            freq[nums[i]]--;
        }

        for(int i = 1; i < n; i++){
            dp[0][i] = max(dp[0][i], dp[0][i - 1]);
        }


        for(int i = 1; i <= k; i++){
            int maxi = dp[i - 1][0];

            unordered_map<int, int> mp;
            mp[nums[0]] = 1;

            dp[i][0] = maxi;
            for(int j = 1; j < n; j++){
                dp[i][j] = max(maxi + 1, mp[nums[j]] + 1);
                maxi = max(maxi, dp[i - 1][j]);

                mp[nums[j]] = dp[i][j];
            }
        }

        int ans = 0;
        for(int i = 0; i <= k; i++){
            for(int j = 0; j < n; j++){
                ans = max(ans, dp[i][j]);
            }    
        }

        // for(int i = 0; i <= k; i++){
        //     for(int j = 0; j < n; j++){
        //         cout << dp[i][j] << " ";
        //     }    
        //     cout << endl;
        // }
        return ans;
    }
};
