class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        if k == 0:
            cnt = Counter(nums)
            return cnt.most_common(1)[0][1]
        hmap, ans, buf = {}, 1, [0 for _ in range(k + 1)]
        for num in nums:
            if num not in hmap:
                hmap[num] = [0] * (k + 1)
            li = hmap[num]
            li[0] += 1
            for i in range(1, k + 1):
                li[i] = 1 + max(li[i], buf[i - 1])
            for i in range(k + 1):
                if li[i] > buf[i]:
                    buf[i] = li[i]
                    ans = max(ans, li[i])
        return ans
