class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>> dp(n, vector<int>(k+1,1));
        vector<vector<int>> ans(n, vector<int>(k+1,1));
        map<int,int> mp;
        mp[nums[0]]=0;
        for(int i=1;i<n;i++){
            for(int j=0;j<=k;j++){
                if(j>0) dp[i][j] = ans[i-1][j-1]+1;
                if(mp.find(nums[i])!=mp.end()){
                    int x=mp[nums[i]];
                    dp[i][j]=max(dp[i][j] , dp[x][j]+1);
                }
                ans[i][j] = max(ans[i-1][j] , dp[i][j]);
            }
            mp[nums[i]]=i; 
        }
        return ans[n-1][k];
    }
};
