class Solution {
public:
    int maximumLength(vector<int>& v, int k) {
        int n= v.size();
        vector<vector<int>>dp(n,vector<int>(k+1,0));
        vector<pair<int,unordered_map<int,int>>> a(k+1);
        for(int i=0;i<=k;i++){
            a[i].first = 1;
            a[i].second[v[n-1]] = 1;
            dp[n-1][i] = 1;
        }
        for(int i=n-2;i>=0;i--){
            for(int j=k;j>=0;j--){
                if(j == 0)dp[i][j] = 1;
                else dp[i][j] = 1+a[j-1].first;
                dp[i][j] = max(dp[i][j],1+a[j].second[v[i]]);
                a[j].first = max(a[j].first,dp[i][j]);
                a[j].second[v[i]] = max(a[j].second[v[i]],dp[i][j]);
            }
        }
        int mx = 1;
        for(int i=0;i<n;i++){
            mx = max(mx,dp[i][k]);
        }
        return mx;
    }
};
