class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int> > dp(n,vector<int>(k+1,0));

        for(int i=0;i<n;i++){
            dp[i][0]=1;
        }

        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                if(nums[i]==nums[j]){
                    dp[j][0]=max(dp[i][0]+1,dp[j][0]);
                }
            }
        }




        for(int l=1;l<=k;l++){

            map<int, int> mp;
            int mx=0;

            dp[0][l]=1;

            for(int i=0;i<n;i++){
                if(mp.find(nums[i])!=mp.end()){
                    int x=mp[nums[i]];
                    dp[i][l]=max(dp[i][l],x+1);
                }
                dp[i][l]=max(dp[i][l],mx+1);
                mx=max(mx,dp[i][l-1]);
                mp[nums[i]]=dp[i][l];
                // cout<<mx<<" ";
            }
            // cout<<endl;
        }





        int ans=1;
        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++){
                ans=max(ans,dp[i][j]);

                // cout<<dp[i][j]<<" ";
            }
            // cout<<endl;

        }
        return ans;
    }
};
