class Solution {
public:
    int maximumLength(vector<int>& nums, int k) { 
        int n=nums.size(),res,i,j,l; 
        vector<vector<int>> maxLeng(n,vector<int>(k+1,1)); 
        vector<int> v(k+1,-1); 
        vector<map<int,int>> mapVec(k+1); 
        res=1; 
        for(i=0;i<n;i++)
        { 
            for(j=0;j<=k;j++)
            { 
                for(l=0;l<j;l++)
                    maxLeng[i][j]=max(maxLeng[i][j],1+v[l]); 
                if(mapVec[j].find(nums[i])!=mapVec[j].end())
                    maxLeng[i][j]=max(maxLeng[i][j],1+mapVec[j][nums[i]]);
                res=max(res,maxLeng[i][j]); 
            } 
            for(int j=0;j<=k;j++)
            { 
                mapVec[j][nums[i]]=max(mapVec[j][nums[i]],maxLeng[i][j]); 
                v[j]=max(v[j],maxLeng[i][j]); 
            } 
        } 
        return res; 
    } 
};
