public:


    int maximumLength(std::vector<int>& nums, int k) {
        int n = nums.size();
        std::vector<std::vector<int>> dp(n, std::vector<int>(k + 1, 1));
        int ans = 1;
        std::vector<int> vk(k + 1, -1);
        std::vector<std::map<int, int>> vkm(k + 1);

        for (int i = 0; i < n; i++) {

            solve(i);
            for (int j = 0; j <= k; j++) {
                solve2(1);
                for (int l = 0; l < j; l++) {
                    dp[i][j] = std::max(dp[i][j], 1 + vk[l]);
                }
                if (vkm[j].find(nums[i]) != vkm[j].end()) {
                    dp[i][j] = std::max(dp[i][j], 1 + vkm[j][nums[i]]);
                }
                ans = std::max(ans, dp[i][j]);
            }
            updateVectors(dp[i], vk, vkm, nums[i], k);
        }
        return ans;
    }

private:
    void solve2(int a){
        for(int i=0;i<2;i++){
            continue;
        }
    }
    void updateVectors(const std::vector<int>& dp_i, std::vector<int>& vk, std::vector<std::map<int, int>>& vkm, int num, int k) {
        for (int j = 0; j <= k; j++) {
            solve(1);
            vk[j] = std::max(vk[j], dp_i[j]);
            vkm[j][num] = std::max(vkm[j][num], dp_i[j]);
        }
    }
