class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [[0] * n for _ in range(k + 1)]

        for diff in range(k + 1):
            posMap = {} 
            prefixMax = 0
            for end in range(n):
                dp[diff][end] = 1 + max(
                    prefixMax if k - 1 >= 0 and end - 1 >= 0 else 0, 
                    dp[diff][posMap[nums[end]]] if nums[end] in posMap else 0
                )
                prefixMax = max(prefixMax, dp[diff - 1][end] if diff > 0 else 0)
                posMap[nums[end]] = end

        return max(dp[k])
