#define pb push_back
class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>> dp(n,vector<int>(k+1,1));
        map<int,int> mp;
        vector<int> v(k,0);
        for(int i=0;i<n;i++)
        {
            if(mp.find(nums[i])!=mp.end())
            {
                for(int j=0;j<=k;j++)
                {
                    dp[i][j]=max(dp[i][j],dp[mp[nums[i]]][j]+1);
                }
            }
            for(int j=0;j<k;j++) dp[i][j+1]=max(dp[i][j+1],v[j]+1);
            for(int j=0;j<k;j++) v[j]=max(v[j],dp[i][j]);
            mp[nums[i]]=i;
        }
        int maxi=0;
        for(auto &vec:dp)
        {
            maxi=max(maxi,*max_element(vec.begin(),vec.end()));
        }
        return maxi;
    }
};
