class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        vector<vi> dp(k+1,vi{0,0});
        vector<set<int>> set(k+1);
        vector<map<int,int>> cnt(k+1);
        for (int num : nums){
           int prev=0;
            rep(j,0,k+1){
              int cur;
              if(set[j].count(num)>0){
                  cur=max(prev+1,(dp[j][0]+1));
              }else{
                  cur=prev+1;
              }
              cur=max(cur,cnt[j][num]+1);
              prev=dp[j][0];
              cnt[j][num]=cur;
              if(dp[j][0]<cur){
                dp[j][0]=cur;
                set[j].clear();
                set[j].insert(num);
              }
              if(cur==dp[j][0]){
                set[j].insert(num);
              }
            }
        }
        return dp[k][0];
    }
};
