class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51, 1));
        int ans = 1;
        vector<int> maxLen(51, -1);
        vector<unordered_map<int, int>> mp(51);

        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= k; j++) {
                for (int l = 0; l < j; l++) 
                    dp[i][j] = max(dp[i][j], 1 + maxLen[l]);

                if (mp[j].find(nums[i]) != mp[j].end()) 
                    dp[i][j] = max(dp[i][j], 1 + mp[j][nums[i]]);

                ans = max(ans, dp[i][j]);
            }

            for (int j = 0; j <= k; j++) {
                maxLen[j] = max(maxLen[j], dp[i][j]);
                mp[j][nums[i]] = max(mp[j][nums[i]], dp[i][j]);
            }
        }

        return ans;
    }
};
