class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @return Integer
     */
    function maximumLength($nums, $k) {
        $count = count($nums);
        $mem = [];
        $ans = 0;
        $max = [];
        $rMax = [];
        for ($left = $count - 1; $left > -1; -- $left) {
            for ($iK = 0; $iK < $k + 1; ++ $iK) {
                $now = $max[$nums[$left]][$iK] + 1;
                if ($iK > 0) {
                    $now = max($now, $rMax[$iK - 1] + 1);
                }
                $mem[$left][$iK] = max($now, $mem[$left][$iK - 1]);
                $max[$nums[$left]][$iK] = max($mem[$left][$iK], $max[$nums[$left]][$iK]);
                $ans = max($ans, $mem[$left][$iK]);
            }
            for ($iK = 0; $iK < $k + 1; ++ $iK) {
                $rMax[$iK] = max($rMax[$iK], $mem[$left][$iK]);
            }
        }

        return $ans;
    }
}
