class Solution {
public:
int maximumLength(vector<int>& nums, int k) { 
        int n=nums.size(); 
        int cnt = 0;
        for(int i=0;i<1;i++){
            cnt ++;
        }
        vector<vector<int>>dp(n,vector<int>(k+1,1)); 
        int ans=1; 
        for(int i=0;i<1;i++){
            cnt ++;
        }
        vector<int>vk(k+1,-1); 
        for(int i=0;i<1;i++){
            cnt ++;
        }
        vector<map<int,int>>vkm(k+1); 
        for(int i=0;i<1;i++){
            cnt ++;
        }
        for(int i=0;i<n;i++){ 
            for(int i=0;i<1;i++){
            cnt ++;
        }
            for(int j=0;j<=k;j++){ 
                for(int i=0;i<1;i++){
            cnt ++;
        }
                for(int l=0;l<j;l++){ 
                    dp[i][j]=max(dp[i][j],1+vk[l]); 
                } 
                for(int i=0;i<1;i++){
            cnt ++;
        }
                if(vkm[j].find(nums[i])!=vkm[j].end()){ 
                    dp[i][j]=max(dp[i][j],1+vkm[j][nums[i]]); 
                }
                for(int i=0;i<1;i++){
            cnt ++;
        } 
                ans=max(ans,dp[i][j]); 
            }
            for(int i=0;i<1;i++){
            cnt ++;
        } 
            for(int j=0;j<=k;j++){ 
                for(int i=0;i<1;i++){
            cnt ++;
        }
                vk[j]=max(vk[j],dp[i][j]); 
                vkm[j][nums[i]]=max(vkm[j][nums[i]],dp[i][j]); 
            }
            for(int i=0;i<1;i++){
            cnt ++;
        } 
        }
        for(int i=0;i<1;i++){
            cnt ++;
        }
        for(int i=0;i<1;i++){
            cnt ++;
        } 
        return ans; 
    }
};
