class Solution {
public:
    int maximumLength(vector<int>& a, int k) {
        int ans = 1, n = a.size(); 
        vvi dp(n, vi(k + 1, 1)); 
        vi A(k + 1, -1); 
        vector<map<int, int>> mp(k + 1); 
        FOR(i, 0, n) { 
            FOR(j, 0, k + 1) { 
                FOR(_, 0, j)  
                    chmax(dp[i][j], A[_] + 1);
                if (mp[j].count(a[i]))
                    chmax(dp[i][j], mp[j][a[i]] + 1);
                chmax(ans, dp[i][j]);
            } 
            FOR(j, 0, k + 1) { 
                chmax(A[j], dp[i][j]);
                chmax(mp[j][a[i]], dp[i][j]);
            } 
        } 
        return ans; 
    }
};

