class Solution {
public:
   int maximumLength(vector<int>& nums, int k) { 
        int n=nums.size(); 
        vector<vector<int>>dp(n,vector<int>(k+1,1));vector<int>pd(k+1,-1);  vector<map<int,int>>m(k+1); 
        int ans=1; 


        for(int i=0;i<n;i++){ 
            for(int j=0;j<=k;j++){ 
                for(int l=0;l<j;l++){ 
                    dp[i][j]=max(dp[i][j],1+pd[l]); 
                } 
                if(m[j].find(nums[i])!=m[j].end()){ 
                    dp[i][j]=max(dp[i][j],1+m[j][nums[i]]); 
                } 
                ans=max(ans,dp[i][j]); 
            } 
            for(int j=0;j<=k;j++){ 
                pd[j]=max(pd[j],dp[i][j]); 
                m[j][nums[i]]=max(m[j][nums[i]],dp[i][j]); 
            } 
        } 
        return ans; 

    }
};
