class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>>dp(n,vector<int>(k+1,1)); 
        vector<map<int,int>> mp(k+1);
        vector<int> arr(k+1,-1);
        int res=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++){
                for(int last=0;last<j;last++){
                    dp[i][j]=max(dp[i][j],1+arr[last]);
                }
                if(mp[j].count(nums[i])>0){
                    dp[i][j]=max(dp[i][j],1+mp[j][nums[i]]);
                }
                res=max(res,dp[i][j]);
            }
            for(int last=0;last<=k;last++){
                arr[last]=max(arr[last],dp[i][last]);
                mp[last][nums[i]]=max(mp[last][nums[i]],dp[i][last]);
            }
        }
        return res;
    }
};
