#define vi vector<int>
#define forn(i,n) for(int i=0;i<n;i++)
#define forab(i,a,b) for(int i = a;i<=b;i++)
class Solution {
public:

    int maximumLength(vector<int>& v, int k) {
                int n = v.size();
            vi count(n+1,0);
            map<int,map<int,int>>mp;
            int ans = 1;
            for(int i=n-1;i>=0;i--){

                    for(int temp = k;temp>=0;temp--){

                            mp[v[i]][temp] = max(mp[v[i]][temp],1+mp[v[i]][temp]);
                            if(temp>0) mp[v[i]][temp] = max(mp[v[i]][temp],1+count[temp-1]);


                            ans = max(ans,count[temp]);
                    }

                    for(int temp = 0;temp<=k;temp++) count[temp] = max(count[temp],mp[v[i]][temp]);


            }
                for(int i=0;i<=k;i++) ans = max(ans,count[i]);

            return ans;
    }
};
