class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        f = [[0]*(k+1) for _ in range(len(nums))]
        match = [defaultdict(int) for _ in range(k+1)]
        unmatch = [0]*(k+1)
        ans = 1
        for i in range(len(nums)):
            f[i][0] = 1
            for j in range(k+1):
                f[i][j] = max(f[i][j], match[j][nums[i]]+1)
                if j > 0:
                    f[i][j] = max(f[i][j], unmatch[j-1]+1)
                ans = max(ans, f[i][j])
            for j in range(k+1):
                match[j][nums[i]] = max(match[j][nums[i]], f[i][j])
                unmatch[j] = max(unmatch[j], f[i][j])
        return ans
