const int N=5e3+5;
int dp[N][55];
int dp2[N][55];
class Solution {
public:

    int maximumLength(vector<int>& nums, int k) {
        unordered_map<int,int> index;
        map<pair<int,int>,int> dict;

        int ans=0;
        for(int i=0;i<nums.size();++i){

            index[nums[i]]++;
            int ind1=index[nums[i]];
            for(int j=k;j>=0;--j){
                if(i==0)dp[i][j]=1;
                else{
                    dp[i][j]=1;
                    if(j){
                        dp[i][j]=max(dp[i][j],1+dp2[i-1][j-1]);
                    }
                    if(dict.find({nums[i],j})!=dict.end()){
                        dp[i][j]=max(dp[i][j],index[nums[i]]+dict[{nums[i],j}]);
                    }
                }
                dict[{nums[i],j}]=max(dict[{nums[i],j}],dp[i][j]-ind1);
                if(j==k)ans=max(ans,dp[i][j]);
                dp2[i][j]=dp[i][j];
                if(i)dp2[i][j]=max(dp2[i-1][j],dp2[i][j]);
            }
        }
        return ans;
    }
};
