class Solution {
public:

    int maximumLength(vector<int>& nums, int k) {

        int n=nums.size();
        unordered_map<int,int> mp;
        vector<vector<int>> dp(n,vector<int>(k+1,0));
        for(int i=0;i<n;i++) dp[i][0]=1;
        int ans=1;
        mp[nums[0]]=0;
        vector<int> mx(k+1,0);
        mx[0]=1;
        for(int i=1;i<n;i++){
            if(mp.find(nums[i])!=mp.end()){
                int j=mp[nums[i]];
                for(int l=0;l<=k;l++){
                    if(dp[i-1][l]!=0) dp[i][l]=max(dp[i][l],1+dp[j][l]);
                    ans=max(ans,dp[i][l]);
                }
            }
            if(nums[i-1]!=nums[i]){
                for(int l=0;l<k;l++){
                    if(mx[l]!=0) dp[i][l+1]=max(dp[i][l+1],1+mx[l]);
                    ans=max(ans,dp[i][l+1]);
                }
            }
            for(int l=0;l<=k;l++){
                mx[l]=max(mx[l],dp[i][l]);
            }
            mp[nums[i]]=i;
        }
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<=k;j++){
        //         cout<<dp[i][j]<<" ";
        //     }
        //     cout<<"\n";
        // }
        return ans;
    }
};
