class Solution {
public:
   int maximumLength(vector<int>& arr, int k) {
    int n=arr.size(),ans=0;
    vector<int> prev(n);
    for(int i=0;i<=k;i++)
    {
        map<int,int> mp;
        vector<int> nex(n),dp(n);
        for(int j=0;j<n;j++)
        {
            if(i>0)
            {
                    if(j>0)
                dp[j]=max(dp[j],prev[j-1]+1);
            }
            dp[j]=max(mp[arr[j]]+1,dp[j]);
            mp[arr[j]]=dp[j];
            ans=max(ans,dp[j]);
            if(j>0)
            nex[j]=max(dp[j],nex[j-1]);
        else
            nex[j]=dp[j];
        }
        prev=nex;
    }
    return ans;
    }
};
