class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [[1] * (k + 1) for _ in range(n)]
        ans = 1
        x = [-1] * (k + 1)
        xx = [{} for _ in range(k + 1)]
        for i in range(n):
            for j in range(k + 1):
                for l in range(j):
                    dp[i][j] = max(dp[i][j], 1 + x[l])
                if nums[i] in xx[j]:
                    dp[i][j] = max(dp[i][j], 1 + xx[j][nums[i]])
                ans = max(ans, dp[i][j])
            for j in range(k + 1):
                x[j] = max(x[j], dp[i][j])
                xx[j][nums[i]] = max(xx[j].get(nums[i], 0), dp[i][j])
        return ans
