class Solution {
public:
    int maximumLength(vector<int>& v, int k) {
        int n=v.size();
        vector<vector<int>> dp(n, vector<int>(k+1, 1));
        for(int i=1; i<n; i++){
            for(int j=0; j<i; j++){
                if(v[j]==v[i]) dp[i][0]=max(dp[i][0], dp[j][0]+1);
            }
        }
        vector<int> maxi1(n);
        maxi1[0]=dp[0][0];
        for(int i=1;i<n;i++){
            maxi1[i]=max(maxi1[i-1], dp[i][0]);
        }
        for(int d=1; d<=k; d++){
            map<int,int> mp;
            vector<int> maxi2(n);
            maxi2[0]=dp[0][d];
            mp[v[0]]=1;
            for(int i=1; i<n; i++){
                dp[i][d]=maxi1[i-1]+1;
                if(mp.find(v[i])!=mp.end()) dp[i][d]=max(dp[i][d], mp[v[i]]+1);
                maxi2[i]=max(maxi2[i-1], dp[i][d]);
                if(mp.find(v[i])==mp.end()||mp[v[i]]<dp[i][d]) mp[v[i]]=dp[i][d];
            }
            maxi1=maxi2;
        }
        int mx=0;
        for(int i=0; i<n; i++){
            mx=max(mx, dp[i][k]);
        }
        // for(auto i: dp){
        //     for(auto j: i) cout<<j<<" "; cout<<endl;
        // }
        return mx;
    }
};
