/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var maximumLength = function(nums, k) {
     const n = nums.length;
        const dp = Array.from({ length: n }, () => Array(k + 1).fill(1));
        let ans = 1;
        const vk = Array(k + 1).fill(-1);
        const vkm = Array.from({ length: k + 1 }, () => ({}));

        for (let i = 0; i < n; i++) {
            for (let j = 0; j <= k; j++) {
                for (let l = 0; l < j; l++) {
                    dp[i][j] = Math.max(dp[i][j], 1 + vk[l]);
                }
                if (nums[i] in vkm[j]) {
                    dp[i][j] = Math.max(dp[i][j], 1 + vkm[j][nums[i]]);
                }
                ans = Math.max(ans, dp[i][j]);
            }
            for (let j = 0; j <= k; j++) {
                vk[j] = Math.max(vk[j], dp[i][j]);
                if (!(nums[i] in vkm[j])) {
                    vkm[j][nums[i]] = dp[i][j];
                } else {
                    vkm[j][nums[i]] = Math.max(vkm[j][nums[i]], dp[i][j]);
                }
            }
        }
        return ans;
};
