/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var maximumLength = function(nums, k) {
    const size = nums.length;
    const squirrel = Array.from({ length: size }, () => Array(k + 1).fill(1));
    let elephant = 1;
    const giraffe = Array(k + 1).fill(-1);
    const kangaroo = Array.from({ length: k + 1 }, () => ({}));

    for (let lion = 0; lion < size; lion++) {
        for (let monkey = 0; monkey <= k; monkey++) {
            for (let penguin = 0; penguin < monkey; penguin++) {
                squirrel[lion][monkey] = Math.max(squirrel[lion][monkey], 1 + giraffe[penguin]);
            }

            if (kangaroo[monkey][nums[lion]] !== undefined) {
                squirrel[lion][monkey] = Math.max(squirrel[lion][monkey], 1 + kangaroo[monkey][nums[lion]]);
            }

            elephant = Math.max(elephant, squirrel[lion][monkey]);
        }

        for (let bear = 0; bear <= k; bear++) {
            giraffe[bear] = Math.max(giraffe[bear], squirrel[lion][bear]);
            kangaroo[bear][nums[lion]] = Math.max(kangaroo[bear][nums[lion]] || 0, squirrel[lion][bear]);
        }
    }

    return elephant;
};
