import java.util.HashMap;

class Solution {
    public int maximumLength(int[] nums, int k) {
        int size = nums.length;
        int[][] lengths = new int[size][k + 1];
        int max_length = 1;
        int[] max_lengths = new int[k + 1];
        HashMap<Integer, Integer>[] num_to_lengths_map = new HashMap[k + 1];

        for (int i = 0; i < size; i++) {
            for (int j = 0; j <= k; j++) {
                lengths[i][j] = 1;
            }
        }

        for (int i = 0; i <= k; i++) {
            num_to_lengths_map[i] = new HashMap<>();
        }

        for (int i = 0; i < size; i++) {
            for (int j = 0; j <= k; j++) {
                for (int l = 0; l < j; l++) {
                    lengths[i][j] = Math.max(lengths[i][j], 1 + max_lengths[l]);
                }

                if (num_to_lengths_map[j].containsKey(nums[i])) {
                    lengths[i][j] = Math.max(lengths[i][j], 1 + num_to_lengths_map[j].get(nums[i]));
                }

                max_length = Math.max(max_length, lengths[i][j]);
            }

            for (int j = 0; j <= k; j++) {
                max_lengths[j] = Math.max(max_lengths[j], lengths[i][j]);
                num_to_lengths_map[j].put(nums[i], Math.max(num_to_lengths_map[j].getOrDefault(nums[i], 0), lengths[i][j]));
            }
        }

        return max_length;
    }
}
