class Solution {
public:
    int maximumLength(vector<int> nums, int k) {
        int n=sz(nums);
        vector<vector<int>> dp(n,vector<int>(k+1,0));
        vector<int> max_yet(k+1,0);
        map<int,array<int,51>> mp;
        dp[0][0]=1;
        max_yet[0]=1;
        mp[nums[0]][0]=1;
        for(int i=1;i<n;i++){
            dp[i][0]=1;
        // debug(max_yet);
        // debug(mp);
        // debug(dp);
            // for(int j=0;j<i;j++){
                for(int l=0;l<k;l++){
                        dp[i][l+1]=max(dp[i][l+1],1+max_yet[l]);
                    }
                // }
                for(int l=0;l<=k;l++){
                    dp[i][l]=max(dp[i][l],1+mp[nums[i]][l]);
                    mp[nums[i]][l]=max(mp[nums[i]][l],dp[i][l]);
                }
                    for(int l=0;l<=k;l++){
                        max_yet[l]=max(max_yet[l],dp[i][l]);
                    }
                // }
            // }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++){
                ans=max(ans,dp[i][j]);
            }
        }
        return ans;
    }
};
