class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();

        vector<unordered_map<int, int>> dp(k+5);
        vector<vector<int>> maxis(k+5, {-1, 0, -1, 0});

        for(int i = 0; i<n; i++){
            for(int j = k; j>=0; j--){
                if(j == 0){
                    dp[j][nums[i]]++;
                } else {
                    dp[j][nums[i]]++;

                    int maxi = 0;
                    if(maxis[j-1][0] == nums[i]){
                        maxi = maxis[j-1][3];
                    } else {
                        maxi = maxis[j-1][1];
                    }
                    dp[j][nums[i]] = max(dp[j][nums[i]], 1+maxi);
                }
                if(dp[j][nums[i]] > maxis[j][1]){
                    maxis[j][2] = maxis[j][0];
                    maxis[j][3] = maxis[j][1];
                    maxis[j][0] = nums[i];
                    maxis[j][1] = dp[j][nums[i]];
                } else if(dp[j][nums[i] > maxis[j][3]]){
                    maxis[j][2] = nums[i];
                    maxis[j][3] = dp[j][nums[i]];
                }
            }
        }

        int ans = 0;
        for(auto k : maxis){
            ans = max(ans, k[1]);
        }

        return ans;
    }
};
