class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
         set<int>st;
        int n=nums.size();
        map<int,int>mp;
        for(auto &j:nums){
            st.insert(j);
        }
        int cnt = 0;
        for(auto &j:st){
            mp[j]=cnt+1;
            cnt++;
        }
        for(auto &j:nums){
            j=mp[j];
        }
        int m=st.size();
        vector<vector<int>>dp(m+1, vector<int>(k+1, 0));
        vector<int>ma(k+1,0);
        for(int i=1;i<=n;i++){
            int x=nums[i-1];
            for(int j=k;j>=1;j--){
                dp[x][j]=max({dp[x][j]+1, ma[j-1]+1});
                ma[j]=max(ma[j], dp[x][j]);
            }
            dp[x][0]=dp[x][0]+1;
            ma[0]=max(ma[0], dp[x][0]);

        }
        int ans = 0;
        for(auto &j:ma){
            ans=max(j, ans);
        }
        return ans;
    }
};
