class Solution {
public:
   // int dp[5001][51];
    int maximumLength(vector<int>& nums, int k) {
       // memset(dp,1,sizeof(dp));
        int n = nums.size(),res = 1;
        vector<vector<int>>dp(n,vector<int>(k+1,1));
        vector<int> A(k+1,-1);
        vector<map<int,int>> mp(k+1);
        for(int i =0;i<n;i++){
            for(int j =0;j<=k;j++){
                for(int p = 0;p<j;p++){
                    dp[i][j] = max(dp[i][j],1+A[p]);
                }
                if(mp[j].count(nums[i])){
                    dp[i][j] = max(dp[i][j],mp[j][nums[i]]+1);
                }
                res = max(res,dp[i][j]);
            }
            for(int j =0;j<=k;j++){
                A[j] = max(A[j],dp[i][j]);
                mp[j][nums[i]] = max(mp[j][nums[i]],dp[i][j]);
            }
        }
        return res;
    }
};
