class Solution {
public:
    int maximumLength(vector<int>& nums, int k) 
    { 
        int n=nums.size(); 
        vector<vector<int>>dp(n,vector<int>(k+1,1)); 
        int ans=1; 
        vector<int>arr(k+1,-1); 
        vector<map<int,int>>parry(k+1); 
        for(int i=0;i<n;i++)
        { 
            for(int j=0;j<=k;j++)
            { 
                for(int q=0;q<j;q++)
                    dp[i][j]=max(dp[i][j],arr[q]+1); 
                if(parry[j].find(nums[i])!=parry[j].end())
                    dp[i][j]=max(dp[i][j],parry[j][nums[i]]+1); 
                ans=max(ans,dp[i][j]); 
            } 
            for(int j=0;j<=k;j++)
            { 
                arr[j]=max(arr[j],dp[i][j]); 
                parry[j][nums[i]]=max(parry[j][nums[i]],dp[i][j]); 
            } 
        } 
        return ans; 
    }
};
