class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        size = len(nums)
        ln = [[1] * (k+1) for i in range(size)]
        mx = 1
        big = [-1] * (k+1)
        pcln = [{} for i in range(k+1)]

        for i in range(size):
            for j in range(k+1):
                for l in range(j):
                    ln[i][j] = max(ln[i][j], 1+big[l])
                if nums[i] in pcln[j]:
                    ln[i][j] = max(ln[i][j], 1+pcln[j][nums[i]])
                mx = max(mx, ln[i][j])

            for j in range(k+1):
                big[j] = max(big[j], ln[i][j])
                pcln[j][nums[i]] = max(pcln[j].get(nums[i], 0), ln[i][j])

        return mx
