class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size(); 
        vector<vector<int>>dp(n,vector<int>(k+1,1)); 
        int res=0;
        res=1;
        vector<int>v(k+1,-1); 
        vector<map<int,int>>mp(k+1); 
        #pragma unroll
        for(int i=0;i<n;i++){ 
            #pragma unroll
            for(int j=0;j<=k;j++){ 
                #pragma unroll
                for(int l=0;l<j;l++){ 
                    dp[i][j]=max(dp[i][j],1+v[l]); 
                } 
                if(mp[j].count(nums[i])){ 
                    dp[i][j]=max(dp[i][j],1+mp[j][nums[i]]); 
                } 
                res=max(res,dp[i][j]); 
            } 
            #pragma unroll
            for(int j=0;j<=k;j++){ 
                v[j]=max(v[j],dp[i][j]); 
                mp[j][nums[i]]=max(mp[j][nums[i]],dp[i][j]); 
            } 
        } 
        return res; 
    }
};
