class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        acc = defaultdict(lambda: defaultdict(lambda: 0))
        for num in nums:
            new_acc = defaultdict(lambda: 0)
            for goodness in range(k + 1):
                use_same = acc[goodness][num] + 1
                use_diff = acc[goodness - 1][-1] + 1
                new_acc[goodness] = max(use_same, use_diff)
            for goodness in range(k + 1):
                acc[goodness][num] = new_acc[goodness]
                acc[goodness][-1] = max(acc[goodness][num], acc[goodness][-1])

        return max(acc[goodness].values())
