class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
      int n=nums.size();
        vector<vector<int>>dp(n,vector<int>(k+1,1));
        int res=1;
        vector<int>v(k+1,-1);
        vector<map<int,int>>v1(k+1);
        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++){
                for(int l=0;l<j;l++){
                    dp[i][j]=max(dp[i][j],1+v[l]);
                }

                if(v1[j].find(nums[i])!=v1[j].end()){
                    dp[i][j]=max(dp[i][j],1+v1[j][nums[i]]);
                }

                res=max(res,dp[i][j]);
            }
            for(int j=0;j<=k;j++){
                v[j]=max(v[j],dp[i][j]);
                v1[j][nums[i]]=max(v1[j][nums[i]],dp[i][j]);
            }
        }
        int c=res;
        return c;
    }
};
