/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var maximumLength = function (nums, k) {
    const n = nums.length;
    const dp = new Array(n).fill(null).map(() => new Array(k + 1).fill(1));
    let ans = 1;
    const arr1 = new Array(k + 1).fill(-1);
    const arr2 = new Array(k + 1).fill(null).map(() => new Map());

    for (let i = 0; i < n; i++) {
        for (let j = 0; j <= k; j++) {
            for (let l = 0; l < j; l++) {
                dp[i][j] = Math.max(dp[i][j], 1 + arr1[l]);
            }
            if (arr2[j].has(nums[i])) {
                dp[i][j] = Math.max(dp[i][j], 1 + arr2[j].get(nums[i]));
            }
            ans = Math.max(ans, dp[i][j]);
        }
        for (let j = 0; j <= k; j++) {
            arr1[j] = Math.max(arr1[j], dp[i][j]);
            arr2[j].set(nums[i], Math.max(arr2[j].get(nums[i]) || 0, dp[i][j]));
        }
    }
    return ans;
};
