class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        lst = [[1] * (k + 1) for _ in range(n)]
        res = 1
        lst1 = [-1] * (k + 1)
        lst2 = [{} for _ in range(k + 1)]
        for i in range(n):
            for j in range(k + 1):
                for l in range(j):
                    lst[i][j] = max(lst[i][j], 1 + lst1[l])
                if nums[i] in lst2[j]:
                    lst[i][j] = max(lst[i][j], 1 + lst2[j][nums[i]])
                res = max(res, lst[i][j])
            for k in range(k + 1):
                lst1[k] = max(lst1[k], lst[i][k])
                lst2[k][nums[i]] = max(lst2[k].get(nums[i], 0), lst[i][k])
        return res
